import React, { useState } from "react";
import {
  Avatar,
  Button,
  CssBaseline,
  TextField,
  Link,
  Paper,
  Grid,
  Typography,
  createTheme,
  ThemeProvider
} from "@mui/material";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import { styled } from "@mui/system";
import { useNavigate } from "react-router-dom";

const theme = createTheme();

const Root = styled(Grid)({
  height: "100vh"
});

const Image = styled(Grid)({
  backgroundImage: "url(https://source.unsplash.com/random)",
  backgroundRepeat: "no-repeat",
  backgroundSize: "cover",
  backgroundPosition: "center"
});

const PaperWrapper = styled("div")({
  margin: "32px",
  display: "flex",
  flexDirection: "column",
  alignItems: "center"
});

const StyledAvatar = styled(Avatar)({
  margin: "8px",
  backgroundColor: "secondary"
});

const StyledForm = styled("form")({
  width: "100%",
  marginTop: "8px"
});

const SignInSide = () => {
  const navigate = useNavigate();
  const [errorMessage, setErrorMessage] = useState(""); // State for error message

  const handleSubmit = async (event) => {
    event.preventDefault();
    setErrorMessage(""); // Clear previous errors

    const formData = new FormData(event.currentTarget);
    const email = formData.get("email");
    const password = formData.get("password");

    try {
      const response = await fetch("http://localhost:5001/api/signin", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password })
      });

      if (response.ok) {
        const data = await response.json();
        console.log("Successfully signed in:", data);
        navigate("/homepage");
      } else {
        const errorData = await response.json();
        setErrorMessage(errorData.message || "Invalid email or password."); // Show error message
      }
    } catch (error) {
      console.error("Sign-in error:", error);
      setErrorMessage("An error occurred. Please try again.");
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Root container component="main">
        <CssBaseline />
        <Image item xs={false} sm={4} md={7} />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <PaperWrapper>
            <StyledAvatar>
              <LockOutlinedIcon />
            </StyledAvatar>
            <Typography component="h1" variant="h5">
              Sign in
            </Typography>

            <StyledForm noValidate onSubmit={handleSubmit}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email Address"
                name="email"
                autoComplete="email"
                autoFocus
              />
              <TextField
                margin="normal"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
                error={Boolean(errorMessage)} // Show error styling if sign-in fails
                helperText={errorMessage} // Display error message under password field
              />
              <SubmitButton
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
              >
                Sign In
              </SubmitButton>
              <Grid container>
                <Grid item>
                  <Link href="/signup" variant="body2">
                    {"Don't have an account? Sign Up"}
                  </Link>
                </Grid>
              </Grid>
            </StyledForm>
          </PaperWrapper>
        </Grid>
      </Root>
    </ThemeProvider>
  );
};

export default SignInSide;
